// Componente do Mapa
export const BrazilStatesMap: React.FC<BrazilStatesMapProps> = ({
  currentCity,
  onCitySelect,
  isLocked = false,
}) => {
  const activeState = cityToState[currentCity];
  const [hoveredState, setHoveredState] = useState<string | null>(null);

  // Helper para clicar no estado
  const handleStateClick = (uf: string) => {
    if (isLocked) return;

    // Encontra a cidade correspondente a este estado para selecionar
    const city = Object.keys(cityToState).find(
      (key) => cityToState[key] === uf,
    );
    if (city) {
      onCitySelect(city);
    }
  };

  // Função de Cor de Preenchimento
  const getFillColor = (uf: string) => {
    if (activeState === uf) return "#008d5e"; // Brand Primary (Verde MedSênior)
    if (
      hoveredState === uf &&
      !isLocked &&
      cityToState[
        Object.keys(cityToState).find((key) => cityToState[key] === uf) || ""
      ]
    )
      return "#a4cd39"; // Brand Accent (Verde Lima) no Hover
    return "#d1d5db"; // Gray 300 (Default)
  };

  // Função de Opacidade
  const getOpacity = (uf: string) => {
    if (activeState === uf) return 1;
    if (isLocked) return 0.4; // Mais apagado se estiver bloqueado em outro lugar
    return 1;
  };

  // Verifica se o estado é interativo (tem cidade mapeada)
  const isStateInteractive = (uf: string) => {
    return Object.values(cityToState).includes(uf);
  };

  return (
    <div className="relative w-full max-w-[450px] mx-auto aspect-[612/640] select-none">
      <svg
        viewBox="0 0 612 640"
        xmlns="http://www.w3.org/2000/svg"
        className="w-full h-full drop-shadow-lg"
        style={{ overflow: "visible" }}
      >
        {statesData.map((state) => {
          const interactive = isStateInteractive(state.id);
          const isActive = activeState === state.id;

          return (
            <path
              key={state.id}
              id={`state-${state.id}`}
              d={state.d}
              fill={getFillColor(state.id)}
              fillOpacity={getOpacity(state.id)}
              stroke="white"
              strokeWidth={isActive ? "2" : "1"}
              strokeLinejoin="round"
              onMouseEnter={() => interactive && setHoveredState(state.id)}
              onMouseLeave={() => interactive && setHoveredState(null)}
              onClick={() => interactive && handleStateClick(state.id)}
              className={`transition-all duration-300 ease-out outline-none ${
                interactive && !isLocked
                  ? "cursor-pointer hover:z-10"
                  : "cursor-default"
              } ${isActive ? "z-20 scale-[1.01] drop-shadow-md" : "z-0"}`}
              style={{
                transformOrigin: "center",
              }}
            />
          );
        })}
      </svg>

      {/* Tooltip / Tag Flutuante */}
      {activeState && stateCenters[activeState] && (
        <div
          className="absolute z-30 transform -translate-x-1/2 -translate-y-full pointer-events-none"
          style={{
            left: `${(stateCenters[activeState].x / 612) * 100}%`,
            top: `${(stateCenters[activeState].y / 640) * 100}%`,
            marginTop: "-10px", // Elevar um pouco acima do centro
          }}
        >
          <div className="relative bg-brand-dark text-white px-4 py-2 rounded-lg shadow-xl border border-brand-primary/30 flex flex-col items-center animate-fade-in min-w-[110px]">
            <span className="text-[10px] font-bold uppercase tracking-widest mb-0.5 opacity-80">
              {activeState}
            </span>
            <span className="font-bold whitespace-nowrap text-sm">
              {currentCity}
            </span>

            {/* Seta do Tooltip */}
            <div className="w-3 h-3 bg-brand-dark border-r border-b border-brand-primary/30 rotate-45 absolute -bottom-1.5 left-1/2 -translate-x-1/2"></div>
          </div>
        </div>
      )}

      {/* Indicador de Bloqueio */}
      {isLocked && (
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 bg-white/90 backdrop-blur-sm px-4 py-1.5 rounded-full shadow-sm border border-gray-200 text-xs font-bold text-gray-500 uppercase tracking-wider pointer-events-none">
          Localização Fixa
        </div>
      )}
    </div>
  );
};
